insomnia.test('Each round log follows expected patterns', () => {
  const responseBody = insomnia.response.json();

  // Filter lines for rounds
  const roundLines = responseBody.filter(line => line.startsWith('>>> Round '));
  roundLines.forEach(line => {
    insomnia.expect(line).to.match(/^>>> Round \d+ <<<$/);
  });

  // Validate card play logs
  const playLines = responseBody.filter(line => line.includes('plays Card'));
  playLines.forEach(line => {
    insomnia.expect(line).to.match(/^\w+ plays Card [\w\s]+!$/);
  });

  // Validate card details
  const cardDetails = responseBody.filter(line => line.startsWith('Card Type:'));
  cardDetails.forEach(line => {
    insomnia.expect(line).to.match(/^Card Type: (Monster|Spell) \| Damage: \d+(\.\d+)? \| Element Type: \w+$/);
  });

  // Validate outcomes (win or tie)
  const outcomes = responseBody.filter(line => line.includes('has won Round') || line.includes("it's a tie"));
  outcomes.forEach(line => {
    insomnia.expect(line).to.match(/^(\w+ has won Round \d+!|No one has won Round \d+ - it's a tie!)$/);
  });
});


