  insomnia.expect(jsonBody.message).to.eql('Package created successfully');
});

insomnia.test('Check if AddedCards array exists', () => {
  const jsonBody = insomnia.response.json();
  insomnia.expect(jsonBody).to.have.property('AddedCards');
  insomnia.expect(jsonBody.AddedCards).to.be.an('array');
});

insomnia.test('Check if AddedCards array contains 5 items', () => {
  const addedCards = insomnia.response.json().AddedCards;
  insomnia.expect(addedCards).to.have.lengthOf(5);
});

insomnia.test('Validate structure and format of each card', () => {
  const addedCards = insomnia.response.json().AddedCards;

  addedCards.forEach(card => {
    // Check required properties
    insomnia.expect(card).to.have.all.keys(
      'CardId',
      'CardName',
      'Damage',
      'CardType',
      'ElementType'
    );

    // Validate types of properties
    insomnia.expect(card.CardId).to.be.a('string');
    insomnia.expect(card.CardName).to.be.a('string');
    insomnia.expect(card.Damage).to.be.a('number');
    insomnia.expect(card.CardType).to.be.oneOf(['Monster Card', 'Spell Card']);
    insomnia.expect(card.ElementType).to.be.oneOf(['Fire', 'Water', 'Normal']);
  });
});

insomnia.test('Status is 201', () => {
  insomnia.expect(insomnia.response.code).to.eql(201);
