// Shuffle the array to get random IDs
function shuffle(array) {
    for (let i = array.length - 1; i > 0; i--) {
        let j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
}
shuffle(cardIds);

// Select the first 4 IDs
let selectedIds = cardIds.slice(0, 4);

// Save the selected IDs as a stringified array
insomnia.environment.set("deckIds_kienboec", JSON.stringify(selectedIds));




