    const jsonResponse = insomnia.response.json();
    const cards = jsonResponse.Cards;

    // Check that the Cards array exists and is an array
    insomnia.expect(cards).to.be.an('array');
    insomnia.expect(cards.length).to.be.greaterThan(0);
});

